import React, { useState, useEffect } from "react";
// import http from "../http"
import { Link } from "react-router-dom";

export default function Home() {
  const [users, setUsers] = useState([]);

  // You may need to fetch the user data using useEffect and update the state.

  useEffect(() => {
    // Fetch user data using http or any other method
    // Example:
    // http.get("/users").then(response => setUsers(response.data));
  }, []);

  const deleteUser = (userId) => {
    // Implement the logic to delete a user
    // Example:
    // http.delete(`/users/${userId}`).then(response => {
    //   // Handle successful deletion
    //   // Update the state to reflect the changes
    //   setUsers(users.filter(user => user.id !== userId));
    // }).catch(error => {
    //   // Handle error
    //   console.error("Error deleting user:", error);
    // });
  };

  return (
    <div>
      <h2>Users listing...</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Sno.</th>
            <th>Name</th>
            <th>Email</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user, index) => (
            <tr key={user.id}>
              <td>{++index}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>
                <Link className="btn btn-info" to={{ pathname: "/edit/" + user.id }}>
                  Edit
                </Link>
                &nbsp;
                <Link className="btn btn-primary" to={{ pathname: "/view/" + user.id }}>
                  View
                </Link>
                &nbsp;
                <button
                  type="button"
                  className="btn btn-danger"
                  onClick={() => {
                    deleteUser(user.id);
                  }}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
