import React, { useState } from 'react';

const Registration = () => {
  const [fullName, setFullName] = useState('');
  const [contactNumber, setContactNumber] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [university, setUniversity] = useState('');
  const [degree, setDegree] = useState('');
  const [major, setMajor] = useState('');
  const [graduationYear, setGraduationYear] = useState('');
  const [higherUniversity, setHigherUniversity] = useState('');
  const [higherDegree, setHigherDegree] = useState('');
  const [higherMajor, setHigherMajor] = useState('');
  const [higherGraduationYear, setHigherGraduationYear] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Add your form submission logic here
    console.log('Form submitted:', {
      fullName,
      contactNumber,
      email,
      password,
      university,
      degree,
      major,
      graduationYear,
      higherUniversity,
      higherDegree,
      higherMajor,
      higherGraduationYear,
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Sign Up</h3>

      <div className="row">
        {/* Personal Details */}
        <div className="col-md-6 mb-3">
          <label>Full Name</label>
          <input
            type="text"
            className="form-control"
            placeholder="Full name"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
          />
        </div>
        <div className="col-md-6 mb-3">
          <label>Contact Number</label>
          <input
            type="text"
            className="form-control"
            placeholder="Contact number"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
          />
        </div>
      </div>

      <div className="row">
        <div className="col-md-6 mb-3">
          <label>Email address</label>
          <input
            type="email"
            className="form-control"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="col-md-6 mb-3">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
      </div>

      <div className="mb-3">
        <h4>Undergraduate Details</h4>
        <div className="row">
          {/* University Field */}
          <div className="col-md-6 mb-3">
            <label>University</label>
            <input
              type="text"
              className="form-control"
              placeholder="University name"
              value={university}
              onChange={(e) => setUniversity(e.target.value)}
            />
          </div>
          {/* Degree Field */}
          <div className="col-md-6 mb-3">
            <label>Degree</label>
            <select
              className="form-control"
              value={degree}
              onChange={(e) => setDegree(e.target.value)}
            >
              <option value="">Select Degree ↓</option>
              <option value="post-graduate">Post Graduate</option>
              <option value="graduate">Graduate</option>
              <option value="higher-secondary">Higher Secondary</option>
            </select>
          </div>
        </div>

        <div className="row">
          {/* Major Field */}
          <div className="col-md-6 mb-3">
            <label>Major</label>
            <input
              type="text"
              className="form-control"
              placeholder="Major"
              value={major}
              onChange={(e) => setMajor(e.target.value)}
            />
          </div>
          {/* Graduation Year Field */}
          <div className="col-md-6 mb-3">
            <label>Graduation Year</label>
            <input
              type="text"
              className="form-control"
              placeholder="Graduation year"
              value={graduationYear}
              onChange={(e) => setGraduationYear(e.target.value)}
            />
          </div>
        </div>
      </div>

      <div className="mb-3">
        <h4>Higher Education Details</h4>
        <div className="row">
          {/* Higher University Field */}
          <div className="col-md-6 mb-3">
            <label>Higher University</label>
            <input
              type="text"
              className="form-control"
              placeholder="Higher university name"
              value={higherUniversity}
              onChange={(e) => setHigherUniversity(e.target.value)}
            />
          </div>
          {/* Higher Degree Field */}
          <div className="col-md-6 mb-3">
            <label>Higher Degree</label>
            <select
              className="form-control"
              value={higherDegree}
              onChange={(e) => setHigherDegree(e.target.value)}
            >
              <option value="">Select Higher Degree ↓</option>
              <option value="doctorate">Doctorate</option>
              <option value="masters">Masters</option>
              <option value="post-graduate-diploma">Post Graduate Diploma</option>
            </select>
          </div>
        </div>

        <div className="row">
          {/* Higher Major Field */}
          <div className="col-md-6 mb-3">
            <label>Higher Major</label>
            <input
              type="text"
              className="form-control"
              placeholder="Higher major"
              value={higherMajor}
              onChange={(e) => setHigherMajor(e.target.value)}
            />
          </div>
          {/* Higher Graduation Year Field */}
          <div className="col-md-6 mb-3">
            <label>Higher Graduation Year</label>
            <input
              type="text"
              className="form-control"
              placeholder="Higher graduation year"
              value={higherGraduationYear}
              onChange={(e) => setHigherGraduationYear(e.target.value)}
            />
          </div>
        </div>
      </div>

      <div className="d-grid">
        <button type="submit" className="btn btn-primary">
          Sign Up
        </button>
      </div>

      <p className="forgot-password text-right">
        Already registered <a href="/sign-in">sign in?</a>
      </p>
    </form>
  );
};

export default Registration;
