import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserList = () => {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8000/api/profile') 
            .then(response => setUsers(response.data.users))
            .catch(error => console.error('Error fetching users:', error));
    }, []);

    const usersInRows = [];
    const rowSize = 5;

    // Create rows with up to 5 users in each
    for (let i = 0; i < users.length; i += rowSize) {
        const row = users.slice(i, i + rowSize);
        usersInRows.push(row);
    }

    return (
        <div>
            {usersInRows.map((row, rowIndex) => (
                <div key={rowIndex} className="user-row">
                    {row.map((user, index) => (
                        <div key={index} className="user-box">
                            <p>{user.name}</p>
                            <p>{user.contact_number}</p>
                            {/* Additional user details can be displayed here */}
                        </div>
                    ))}
                </div>
            ))}
        </div>
    );
};

export default UserList;
