import React, { useState } from "react";

const EditProfile = ({ location }) => {
  // Initialize editedData with an empty object
  const [editedData, setEditedData] = useState({});

  // Check if location and location.state are defined
  if (!location || !location.state) {
    // Handle the case when location or location.state is undefined
    return (
      <div>
        <p>Error: User data not available.</p>
      </div>
    );
  }

  const userData = location.state;

  // Update editedData inside the if block
  setEditedData({ ...userData });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSaveChanges = () => {
    // Send API request to update user data using editedData
    // Implement the logic to update the user data via your Laravel API
    console.log("Edited Data:", editedData);
    // Add your API call here
  };

  return (
    <div>
      <h2>Edit Profile</h2>
      <form>
        {/* Add input fields for editing */}
        <div className="mb-3">
          <label htmlFor="name" className="form-label">Name</label>
          <input
            type="text"
            className="form-control"
            id="name"
            name="name"
            value={editedData.name || ''}
            onChange={handleInputChange}
          />
        </div>
        {/* Add other input fields similarly */}
        <button type="button" className="btn btn-primary" onClick={handleSaveChanges}>
          Save Changes
        </button>
      </form>
    </div>
  );
};

export default EditProfile;
