import React, { useState } from 'react';
import axios from 'axios';

const Registration = () => {
  const [fullName, setFullName] = useState('');
  const [contactNumber, setContactNumber] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [higherUniversity, setHigherUniversity] = useState('');
  const [higherDegree, setHigherDegree] = useState('');
  const [higherMajor, setHigherMajor] = useState('');
  const [higherGraduationYear, setHigherGraduationYear] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const userData = {
      name: fullName,
      contact_number: contactNumber,
      email: email,
      password: password,
      higher_university: higherUniversity,
      higher_degree: higherDegree,
      higher_major: higherMajor,
      higher_graduation: higherGraduationYear,
    };

    try {
      const response = await axios.post('http://localhost:8000/api/user', userData);
      console.log('User data posted:', response.data);
      // Handle success, update state or show a success message
    } catch (error) {
      console.error('Error posting user data:', error);
      // Handle error, show an error message, etc.
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Sign Up</h3>

      <div className="row">
        {/* Personal Details */}
        <div className="col-md-6 mb-3">
          <label>Full Name</label>
          <input
            type="text"
            className="form-control"
            placeholder="Full name"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
          />
        </div>
        <div className="col-md-6 mb-3">
          <label>Contact Number</label>
          <input
            type="text"
            className="form-control"
            placeholder="Contact number"
            value={contactNumber}
            onChange={(e) => setContactNumber(e.target.value)}
          />
        </div>
      </div>

      <div className="row">
        <div className="col-md-6 mb-3">
          <label>Email address</label>
          <input
            type="email"
            className="form-control"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="col-md-6 mb-3">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
      </div>

      {/* ... Additional form fields for Higher Education Details ... */}

      <div className="d-grid">
        <button type="submit" className="btn btn-primary">
          Sign Up
        </button>
      </div>

      <p className="forgot-password text-right">
        Already registered <a href="/sign-in">sign in?</a>
      </p>
    </form>
  );
};

export default Registration;
